INSERT INTO public.epelia_product_categories (id, name, product_group_id, description) VALUES (11658, 'Frischkäse', 1646, 'Als Frischkäse werden alle Käsesorten bezeichnet, die unmittelbar nach der Herstellung verzehrt werden können und nicht wie Hartkäse, <a href="/weichkaese/" title="Weichkäse">Weichkäse</a> oder <a href="/schnittkaese/" title="Schnittkäse">Schnittkäse</a> reifen müssen. Zum Frischkäse gehören die Sorten Quark, Hüttenkäse, Rahmkäse, Mozzarella, Ricotta und Mascarpone.  Die Konsistenz von Frischkäse ist weich und glatt. Einzige Ausnahme ist der Hüttenkäse. Er hat eine körnige Struktur und wird deshalb auch als körniger Frischkäse bezeichnet. Alle Frischkäsesorten schmecken mild, frisch und haben ein angenehm leichtes Aroma. Der Käse lässt sich gut mit dem Messer verstreichen, er ist der einzige Käse, der keine Rinde besitzt und hat eine weiße oder gelbliche Farbe. Frischkäse ist sehr vielseitig verwendbar. Er eignet sich als <a href="/brotaufstriche/" title="Brotaufstriche">Brotaufstrich</a>, zum Backen, für <a href="/kuchen/" title="Kuchen">Kuchen</a> und die Zubereitung von Desserts. Er harmoniert auch sehr gut mit <a href="/kartoffeln/" title="Kartoffeln">Kartoffel</a> Gerichten und zu <a href="/gemuese/" title="Gemüse">Gemüse</a>. ');
INSERT INTO public.epelia_product_categories (id, name, product_group_id, description) VALUES (11659, 'Mozzarella', 1646, '');
