INSERT INTO public.epelia_product_categories (id, name, product_group_id, description) VALUES (11664, 'Kaffeebohnen', 1647, 'Kaffee ist für viele Menschen mehr als nur ein Getränk. Für manche ist er am Morgen durch seine anregende Wirkung der Wachmacher schlechthin, für andere durch sein unverwechselbares Aroma ein reines Genussmittel. <a href="/kaffee" title="Kaffee">Kaffee</a> begleitet uns durch den Alltag, verwöhnt uns an Fest- und Feiertagen und steht für Gemütlichkeit und Gastfreundschaft.
