INSERT INTO public.epelia_product_categories (id, name, product_group_id, description) VALUES (11666, 'Espresso', 1647, '<a href="/espresso-gemahlen" title="Espresso gemahlen">Gemahlener Espresso</a> oder <a href="/espresso-bohnen" title="Espresso Bohnen">Espresso Bohnen</a> von Privatröstereien aus Deutschland oder im Direktimport aus den großen Kaffee Anbaugebieten in Südamerika und Zentralafrika können Sie bei Kisju bestellen. Espresso wird wie <a href="/kaffee" title="Kaffee">Kaffee</a> aus <a href="/kaffeebohnen" title="Kaffeebohnen">Kaffeebohnen</a> gekocht, allerdings in einem anderen Zubereitungsverfahren. Beim italienischen Espresso wird heißes Wasser mit sehr viel Druck durch das fein gemahlene Espressopulver gepresst. Ein guter Espresso mit einer schönen Crema entsteht mit der richtigen Wassertemperatur, einer korrekten Durchlaufzeit und einer guten Espresso Bohnen Qualität. Der Espresso wird gerne pur getrunken oder mit Milch verfeinert. Man unterscheidet Caffè Latte, Espresso macchiato, Cappuccino und Latte macchiato. Ein Caffè Lattte ist ein Milchkaffee mit einem Espresso und viel heißer Milch. Der Espresso macchiato ist ein Espresso mit einem kleinen Schuss Milch und einer feinen Haube aus Milchschaum. Der beliebte Cappuccino besteht zu je einem Drittel aus Espresso, heißer Milch und aufgeschäumter Milch. Einen optisch imposanten Eindruck hinterlässt der Latte macchiato mit seinen drei Schichten. Die Kaffeezubereitung besteht oben und unten zu zwei Drittel aus heißer Milch, zu einem Drittel aus Milchschaum und einem Espresso in der Zwischenschicht. Im <a href="http://kaffeedramatiker.wordpress.com/lexikon/" target="_blank"title="Kaffeelexikon">Kaffeelexikon</a> finden Sie weitere Einblicke in die Vielfalt und die Kultur von Kaffee und Espresso.');
INSERT INTO public.epelia_product_categories (id, name, product_group_id, description) VALUES (11667, 'Espresso gemahlen', 1647, '');
INSERT INTO public.epelia_product_categories (id, name, product_group_id, description) VALUES (11668, 'Espresso Bohnen', 1647, '');
