INSERT INTO public.epelia_product_categories (id, name, product_group_id, description) VALUES (11821, 'Vollkornnudeln', 1662, '');
INSERT INTO public.epelia_product_categories (id, name, product_group_id, description) VALUES (11822, 'Gnocchi', 1662, '');
