INSERT INTO public.epelia_product_categories (id, name, product_group_id, description) VALUES (11825, 'Brot', 1663, 'Brot und Brötchen morgens frisch vom Bäcker schmecken in den meisten Fällen am besten. Es gibt aber bestimmte Brotsorten, die Sie nicht bei Ihrer <a href="/baeckerei" title="Bäckerei">Bäckerei</a> in der Nähe finden oder Brote, deren Zutaten auf Ernährungsformen und Allergien, wie zum Beispiel Zöliakie zugeschnitten sind. Diese Brot Spezialitäten, Vollkornbrote, Dosenbrote, glutenfreie Brote, Essener Brote, <a href="/dinkelbrot" title="Dinkelbrot">Dinkelbrot</a> oder <a href="/kohlenhydratarm" title="Kohlenhydratarm">kohlenhydratarmes</a> Brot, können Sie deswegen bei unseren Bäckern direkt bestellen. Bei Bestellung wird Ihr Brot frisch gebacken und ist durch die Zugabe von Sauerteig lange haltbar.  Viele Kunden schreiben uns, dass sie überrascht sind über die Frische und Saftigkeit des Brotes nach dem Versand. Probieren auch Sie die Abwechslung des täglich Brot. Neben <a href="/bio-brot" title="Bio Brot">Bio Brot</a> finden Sie in unserem Angebot auch <a href="/weissbrot" title="Weißbrot">Weißbrote</a>, <a href="/schwarzbrot" title="Scharzbrot">Schwarzbrot</a> und <a href="/backmischung" title="Backmischung">Backmischungen</a> für Brote zum Backen für Zuhause. ');
INSERT INTO public.epelia_product_categories (id, name, product_group_id, description) VALUES (11826, 'Brötchen', 1663, '');
